import React, { useState, useEffect, PropTypes } from "react";
import { Doughnut, defaults } from 'react-chartjs-2'
import 'chartjs-plugin-datalabels'
import "./App.css";

import './styles.css'
import { ResponsiveLine } from '@nivo/line'
import { ResponsiveBar } from '@nivo/bar'

import { ComposedChart, Line, Area, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from "recharts"
const dataNivo = [
    {
       "id":"ทรัพย์ที่ศูนย์ฯ ส่งมากำหนดราคา",
       "color":"hsl(58, 100%, 31%)",
       "data":[
          {
             "x":"01/2564",
             "y":166
          },
          {
             "x":"02/2564",
             "y":100
          },
          {
             "x":"03/2564",
             "y":22
          }
       ]
    },
    {
       "id":"ทรัพย์ที่กำหนดราคาแล้ว",
       "color":"hsl(304, 100%, 50%)",
       "data":[
          {
             "x":"01/2564",
             "y":165
          },
          {
             "x":"02/2564",
             "y":80
          },
          {
             "x":"03/2564",
             "y":12
          }
       ]

    }
 ]
const dataBar1 = [

    {
       "ภาค":"ภาค 6",
       "hot dog":36,
       "hot dogColor":"hsl(305, 70%, 50%)",
       "burger":36,
       "burgerColor":"hsl(155, 70%, 50%)",

    },
    {
       "ภาค":"ภาค 7",
       "hot dog":32,
       "hot dogColor":"hsl(299, 70%, 50%)",
       "burger":32,
       "burgerColor":"hsl(165, 70%, 50%)",

    },
    {
       "ภาค":"ภาค 15",
       "hot dog":198,
       "hot dogColor":"hsl(92, 70%, 50%)",
       "burger":177,
       "burgerColor":"hsl(101, 70%, 50%)",

    }
 ]


function App() {

    return (
        <React.Fragment>
            <div id="page-guarantee" className="container-fluid py-0 px-4">
                <div className="bg-contain px-4">
                    <div className="row mt-3">
                        <div className="col-12 pt-4"
                        ><h4 className="title-page" style={{ textAlign: 'center' }}>รายงานสรุปผลการเก็บข้อมูลทรัพย์ NPL - NPA </h4>
                        </div>

                    </div>
                </div>
            </div>


            <div className="bg-contain px-4" style={{ height: 500 }}>
                <ResponsiveLine
                    data={dataNivo}
                    margin={{ top: 50, right: 110, bottom: 50, left: 60 }}
                    xScale={{ type: 'point' }}
                    yScale={{ type: 'linear', min: 'auto', max: 'auto', stacked: false, reverse: false }}
                    yFormat=" >-.2f"
                    axisTop={null}
                    axisRight={null}
                    axisBottom={{
                        orient: 'bottom',
                        tickSize: 0,
                        tickPadding: 5,
                        tickRotation: 0,
                        legend: 'ข้อมูลทรัพย์ NPL ตั้งแต่เดือน 11/2563 - 02/2564',
                        legendOffset: 36,
                        legendPosition: 'middle'
                    }}
                    axisLeft={{
                        orient: 'left',
                        tickSize: 5,
                        tickPadding: 5,
                        tickRotation: 0,
                        legend: 'จำนวนทรัพย์',
                        legendOffset: -40,
                        legendPosition: 'middle'
                    }}
                    pointSize={10}
                    pointColor={{ theme: 'background' }}
                    pointBorderWidth={2}
                    pointBorderColor={{ from: 'serieColor' }}
                    pointLabelYOffset={-12}
                    useMesh={true}
                    legends={[
                        {
                            anchor: 'top',
                            direction: 'column',
                            justify: false,
                            translateX: 100,
                            translateY: 0,
                            itemsSpacing: 0,
                            itemDirection: 'left-to-right',
                            itemWidth: 80,
                            itemHeight: 20,
                            itemOpacity: 1,
                            fontSize: 16,
                            symbolSize: 13,
                            symbolShape: 'circle',
                            symbolBorderColor: 'rgba(0, 0, 0, .5)',
                            effects: [
                                {
                                    on: 'hover',
                                    style: {
                                        itemBackground: 'rgba(0, 0, 0, .03)',
                                        itemOpacity: 1
                                    }
                                }
                            ]
                        }
                    ]}
                />
            </div>
            <div id="page-guarantee" className="container-fluid py-0 px-4">
                <div className="bg-contain px-4">
                    <div className="row mt-3">
                        <div className="col-12 pt-4"
                        ><h4 className="title-page" style={{ textAlign: 'center' }}>รายงานสรุปผลการเก็บข้อมูลทรัพย์ NPL - NPA </h4>
                        </div>

                    </div>
                </div>
            </div>
            <div className="bg-contain px-4" style={{ height: 500 }}>
                <ResponsiveBar
                    data={dataBar1}
                    keys={['hot dog', 'burger']}
                    indexBy="ภาค"
                    margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
                    groupMode="grouped"
                    valueScale={{ type: 'linear' }}
                    indexScale={{ type: 'band', round: true }}
                    colors={{ scheme: 'nivo' }}
                    defs={[
                        {
                            id: 'dots',
                            type: 'patternDots',
                            background: 'inherit',
                            color: '#38bcb2',
                            size: 4,
                            padding: 1,
                            stagger: true
                        },
                        {
                            id: 'lines',
                            type: 'patternLines',
                            background: 'inherit',
                            color: '#eed312',
                            rotation: -45,
                            lineWidth: 6,
                            spacing: 10
                        }
                    ]}
                    fill={[
                        {
                            match: {
                                id: 'fries'
                            },
                            id: 'dots'
                        },
                        {
                            match: {
                                id: 'sandwich'
                            },
                            id: 'lines'
                        }
                    ]}
                    borderColor={{ from: 'color', modifiers: [['darker', 1.6]] }}
                    axisTop={null}
                    axisRight={null}
                    axisBottom={{
                        tickSize: 5,
                        tickPadding: 5,
                        tickRotation: 0,
                        legend: 'ข้อมูลทรัพย์ NPL ส่งเข้ากำหนดรา',
                        legendPosition: 'middle',
                        legendOffset: 32
                    }}
                    axisLeft={{
                        tickSize: 5,
                        tickPadding: 5,
                        tickRotation: 0,
                        legend: 'จำนวนทรัพย์',
                        legendPosition: 'middle',
                        legendOffset: -40
                    }}
                    labelSkipWidth={12}
                    labelSkipHeight={12}
                    labelTextColor={{ from: 'color', modifiers: [['darker', 1.6]] }}
                    legends={[
                        {
                            dataFrom: 'keys',
                            anchor: 'bottom-right',
                            direction: 'column',
                            justify: false,
                            translateX: 120,
                            translateY: 0,
                            itemsSpacing: 2,
                            itemWidth: 100,
                            itemHeight: 20,
                            itemDirection: 'left-to-right',
                            itemOpacity: 0.85,
                            symbolSize: 20,
                            effects: [
                                {
                                    on: 'hover',
                                    style: {
                                        itemOpacity: 1
                                    }
                                }
                            ]
                        }
                    ]}
                    animate={true}
                    motionStiffness={90}
                    motionDamping={15}
                />
            </div>


        </React.Fragment>
    );
}

export default App;
