import React, { useState, useEffect, PropTypes } from "react";
import { Doughnut, defaults } from 'react-chartjs-2'
import 'chartjs-plugin-datalabels'
import "./App.css";

import './styles.css'
import { ResponsiveLine } from '@nivo/line'

import { ComposedChart, Line, Area, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from "recharts"
const dataNivo = [{id:"japan",color:"hsl(27, 70%, 50%)",data:[{x:"plane",y:65},{x:"helicopter",y:240},{x:"boat",y:242},{x:"train",y:13},{x:"subway",y:81},{x:"bus",y:120},{x:"car",y:68},{x:"moto",y:8},{x:"bicycle",y:176},{x:"horse",y:23},{x:"skateboard",y:164},{x:"others",y:16}]},{id:"france",color:"hsl(264, 70%, 50%)",data:[{x:"plane",y:103},{x:"helicopter",y:48},{x:"boat",y:184},{x:"train",y:42},{x:"subway",y:185},{x:"bus",y:48},{x:"car",y:73},{x:"moto",y:85},{x:"bicycle",y:17},{x:"horse",y:64},{x:"skateboard",y:283},{x:"others",y:226}]},{id:"us",color:"hsl(236, 70%, 50%)",data:[{x:"plane",y:60},{x:"helicopter",y:218},{x:"boat",y:293},{x:"train",y:96},{x:"subway",y:114},{x:"bus",y:276},{x:"car",y:27},{x:"moto",y:98},{x:"bicycle",y:227},{x:"horse",y:49},{x:"skateboard",y:169},{x:"others",y:127}]},{id:"germany",color:"hsl(239, 70%, 50%)",data:[{x:"plane",y:33},{x:"helicopter",y:289},{x:"boat",y:20},{x:"train",y:147},{x:"subway",y:140},{x:"bus",y:273},{x:"car",y:51},{x:"moto",y:25},{x:"bicycle",y:58},{x:"horse",y:11},{x:"skateboard",y:228},{x:"others",y:60}]},{id:"norway",color:"hsl(88, 70%, 50%)",data:[{x:"plane",y:112},{x:"helicopter",y:265},{x:"boat",y:58},{x:"train",y:52},{x:"subway",y:289},{x:"bus",y:299},{x:"car",y:216},{x:"moto",y:74},{x:"bicycle",y:257},{x:"horse",y:163},{x:"skateboard",y:125},{x:"others",y:246}]}]
const newData = [{ name: '2563', NPL: (100 * 400 * 12) / (1024 * 1024), NPA: (200 * 250 * 12) / (1024 * 1024), rate: (100 * 400 * 12) / (1024 * 1024) + (200 * 250 * 12) / (1024 * 1024) },
{ name: '2564', NPL: (100 * 800 * 12) / (1024 * 1024), NPA: (200 * 500 * 12) / (1024 * 1024), rate: (100 * 800 * 12) / (1024 * 1024) + (200 * 500 * 12) / (1024 * 1024) },
{ name: '2565', NPL: (100 * 1200 * 12) / (1024 * 1024), NPA: (200 * 750 * 12) / (1024 * 1024), rate: (100 * 1200 * 12) / (1024 * 1024) + (200 * 750 * 12) / (1024 * 1024) },
{ name: '2566', NPL: (100 * 1600 * 12) / (1024 * 1024), NPA: (200 * 1000 * 12) / (1024 * 1024), rate: (100 * 1600 * 12) / (1024 * 1024) + (200 * 1000 * 12) / (1024 * 1024) },
{ name: '2567', NPL: (100 * 2000 * 12) / (1024 * 1024), NPA: (200 * 1250 * 12) / (1024 * 1024), rate: (100 * 2000 * 12) / (1024 * 1024) + (200 * 1250 * 12) / (1024 * 1024) },
{ name: '2568', NPL: (100 * 2400 * 12) / (1024 * 1024), NPA: (200 * 1500 * 12) / (1024 * 1024), rate: (100 * 2400 * 12) / (1024 * 1024) + (200 * 1500 * 12) / (1024 * 1024) },
{ name: '2569', NPL: (100 * 2800 * 12) / (1024 * 1024), NPA: (200 * 1750 * 12) / (1024 * 1024), rate: (100 * 2800 * 12) / (1024 * 1024) + (200 * 1750 * 12) / (1024 * 1024) },
{ name: '2570', NPL: (100 * 3200 * 12) / (1024 * 1024), NPA: (200 * 2000 * 12) / (1024 * 1024), rate: (100 * 3200 * 12) / (1024 * 1024) + (200 * 2000 * 12) / (1024 * 1024) },
{ name: '2571', NPL: (100 * 3600 * 12) / (1024 * 1024), NPA: (200 * 2250 * 12) / (1024 * 1024), rate: (100 * 3600 * 12) / (1024 * 1024) + (200 * 2250 * 12) / (1024 * 1024) },
{ name: '2572', NPL: (100 * 4000 * 12) / (1024 * 1024), NPA: (200 * 2500 * 12) / (1024 * 1024), rate: (100 * 4000 * 12) / (1024 * 1024) + (200 * 2500 * 12) / (1024 * 1024) },
{ name: '2573', NPL: (100 * 4400 * 12) / (1024 * 1024), NPA: (200 * 2750 * 12) / (1024 * 1024), rate: (100 * 4400 * 12) / (1024 * 1024) + (200 * 2750 * 12) / (1024 * 1024) },
{ name: '2574', NPL: (100 * 4800 * 12) / (1024 * 1024), NPA: (200 * 3000 * 12) / (1024 * 1024), rate: (100 * 4800 * 12) / (1024 * 1024) + (200 * 3000 * 12) / (1024 * 1024) },
{ name: '2575', NPL: (100 * 4800 * 12) / (1024 * 1024), NPA: (200 * 3000 * 12) / (1024 * 1024), rate: (100 * 4800 * 12) / (1024 * 1024) + (200 * 3000 * 12) / (1024 * 1024) }];

const newDataBar = {
    labels: [
      'NPL',
      'NPA',
      'คงเหลือ'
    ],

    datasets: [
      {
        backgroundColor: ['#413ea0', '#F13ea0', '#00DDB8'],
        data: [10,20,30]
      }
    ]
  }

function App() {
    const [iteration, setIteration] = useState(0);
    const [start, setStart] = useState(false);
    const [data, setData] = useState(newData);
    const [nplCount, setNplCount] = useState(400);
    const [nplSize, setNplSize] = useState(100);
    const [npaCount, setNpaCount] = useState(250);
    const [npaSize, setNpaSize] = useState(200);
    const [totalNpl , setTotalNpl] = useState(0);
    const [totalNpa , setTotalNpa] = useState(0);
    const [remainTotal, setRemainTotal] = useState(0);
    const [totalDisk,setTotalDisk] = useState(15);
    const [usedDisk,setUsedDisk] = useState(0);
    const [remainDisk,setRemainDisk] = useState(0);
    const [dataBar , setDataBar]= useState(newDataBar)

    const [otherBuy , setOtherBuy]= useState(0)
    const [sellCount , setSellCount]= useState(0)

    useEffect(() => {
        updateData()

    }, []);

    useEffect(() => {
        updateData()
        // eslint-disable-next-line
    }, [nplCount, nplSize, npaCount, npaSize,otherBuy,sellCount]);




    const options = {
        cutoutPercentage: 20,
        maintainAspectRatio: false,
        tooltips: {
          callbacks: {
            label: function(tooltipItem, data) {
              var dataset = data.datasets[tooltipItem.datasetIndex]
              var meta = dataset._meta[Object.keys(dataset._meta)[0]]
              var total = meta.total
              var currentValue = dataset.data[tooltipItem.index]
              var percentage = parseFloat(((currentValue / total) * 100).toFixed(1))
              return currentValue + ' (' + percentage + '%)'
            },
            title: function(tooltipItem, data) {
              return data.labels[tooltipItem[0].index]
            }
          }
        },
        plugins: {
          datalabels: {
            formatter: (value, ctx) => {
              let sum = 0
              let dataArr = ctx.chart.data.datasets[0].data
              dataArr.map(data => {
                sum += data
              })
              let percentage = ((value * 100) / sum).toFixed(2) + '%'
              return percentage
            },
            color: 'white',
            font: {
              size: 14,
              weight: 'bold'
            }
          }
        }
      }

    const updateData = () => {
        let totalNpa = 0, totalNpl = 0, remainTotal  = 0 , usedDisk = 0, remainDisk = 0
        let nplCount1 = nplCount - otherBuy
        let npaCount1 = npaCount - sellCount
        let aa = data.map((item, i) => {



            let index = i + 1
            item.name = item.name
            item.NPL = (nplCount1 * (index * nplSize) * 12) / (1024 * 1024)

            item.NPA = (npaCount1 * (index * npaSize) * 12) / (1024 * 1024)
            item.rate = ((nplCount1 * (index * nplSize) * 12) / (1024 * 1024)) + ((npaCount1 * (index * npaSize) * 12) / (1024 * 1024))

            totalNpl += (nplCount1*12)
            totalNpa += (npaCount1*12)
            usedDisk += ((nplCount1 * (nplSize) * 12) / (1024 * 1024)) + ((npaCount1 * (npaSize) * 12) / (1024 * 1024))


            return item
        })
        remainDisk = totalDisk - usedDisk
        remainTotal = (remainDisk * (1024 * 1024)) / 200



        setTotalNpa(totalNpa)
        setTotalNpl(totalNpl)
        setRemainDisk(remainDisk.toFixed(2))
        setRemainTotal(remainTotal.toFixed(0))
        setUsedDisk(usedDisk.toFixed(2))

        dataBar.datasets[0].data[0] = totalNpl
        dataBar.datasets[0].data[1] = totalNpa
        dataBar.datasets[0].data[2] = remainTotal

        setDataBar(dataBar)
        // let bb  = dataPie.map((item, i) => {
        //     item.NPL =  totalNpl
        //     item.NPA =  totalNpa
        //     item.remain = remainTotal
        //     return item
        // })
        // setDataPie(bb)
        setData(aa)

    }

    return (
        <React.Fragment>
            <div id="page-guarantee" className="container-fluid py-0 px-4">
                <div className="bg-contain px-4">
                    <div className="row mt-3">
                        <div className="col-12 pt-4"
                        ><h4 className="title-page">รายงานสรุปผลการเก็บข้อมูลทรัพย์ NPL - NPA </h4>
                        </div>
                        <div className="col-12 pt-4"
                        >
                            <div className="form-group">
                                <label className="text-disabled">จำนวนทรัพย์ NPL ต่อเดือน </label>
                                <span className="box-input">
                                    <input placeholder="กรุณาระบุ" className="form-control input-class" maxlength="200" type="text" name="asset_group_id" id="asset_group_id" value={nplCount} onChange={e => { setNplCount(e.target.value); }} />
                                </span>
                                <label className="text-disabled">ขนาดไฟล์ต่อทรัพย์(MB)</label>
                                <span className="box-input">
                                    <input placeholder="กรุณาระบุ" className="form-control input-class" maxlength="200" type="text" name="asset_group_id" id="asset_group_id" value={nplSize} onChange={e => { setNplSize(e.target.value); }} />
                                </span>

                            </div>

                        </div>
                        <div className="col-12" >
                            <div className="form-group">
                                <label className="text-disabled">จำนวนทรัพย์ NPA ต่อเดือน </label>
                                <span className="box-input">
                                    <input placeholder="กรุณาระบุ" className="form-control input-class" maxlength="200" type="text" name="asset_group_id" id="asset_group_id" value={npaCount} onChange={e => { setNpaCount(e.target.value); }} />
                                </span>
                                <label className="text-disabled">ขนาดไฟล์ต่อทรัพย์(MB)</label>
                                <span className="box-input">
                                    <input placeholder="กรุณาระบุ" className="form-control input-class" maxlength="200" type="text" name="asset_group_id" id="asset_group_id" value={npaSize} onChange={e => { setNpaSize(e.target.value); }} />
                                </span>

                            </div>
                        </div>
                        <div className="col-12" >
                            <div className="form-group">
                                <label className="text-disabled">บุคคลภายนอกซื้อ ต่อเดือน</label>
                                <span className="box-input">
                                    <input placeholder="กรุณาระบุ" className="form-control input-class" maxlength="200" type="text" name="asset_group_id" id="asset_group_id" value={otherBuy} onChange={e => { setOtherBuy(e.target.value); }} />
                                </span>
                                <label className="text-disabled">ธนาคารขายได้ ต่อเดือน </label>{`             `}
                                <span className="box-input">
                                    <input placeholder="กรุณาระบุ" className="form-control input-class" maxlength="200" type="text" name="asset_group_id" id="asset_group_id" value={sellCount} onChange={e => { setSellCount(e.target.value); }} />
                                </span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <ComposedChart width={1100} height={400} data={data}
                margin={{ top: 20, right: 20, bottom: 20, left: 20 }}>
                <CartesianGrid stroke='#f5f5f5' />
                <XAxis dataKey="name" />
                <YAxis unit="TB" />
                <Tooltip />
                <Legend />
                <Bar dataKey='NPL' stackId='A' barSize={20} fill='#413ea0' />
                <Bar dataKey='NPA' stackId='A' barSize={20} fill='#F13ea0' />

                <Line type='monotone' dataKey='rate' stroke='#ff7300' />
            </ComposedChart>
            <div className="bg-contain px-4">
            <ResponsiveLine
                data={dataNivo}
                margin={{ top: 50, right: 110, bottom: 50, left: 60 }}
                xScale={{ type: 'point' }}
                yScale={{ type: 'linear', min: 'auto', max: 'auto', stacked: true, reverse: false }}
                yFormat=" >-.2f"
                axisTop={null}
                axisRight={null}
                axisBottom={{
                    orient: 'bottom',
                    tickSize: 5,
                    tickPadding: 5,
                    tickRotation: 0,
                    legend: 'transportation',
                    legendOffset: 36,
                    legendPosition: 'middle'
                }}
                axisLeft={{
                    orient: 'left',
                    tickSize: 5,
                    tickPadding: 5,
                    tickRotation: 0,
                    legend: 'count',
                    legendOffset: -40,
                    legendPosition: 'middle'
                }}
                pointSize={10}
                pointColor={{ theme: 'background' }}
                pointBorderWidth={2}
                pointBorderColor={{ from: 'serieColor' }}
                pointLabelYOffset={-12}
                useMesh={true}
                legends={[
                    {
                        anchor: 'bottom-right',
                        direction: 'column',
                        justify: false,
                        translateX: 100,
                        translateY: 0,
                        itemsSpacing: 0,
                        itemDirection: 'left-to-right',
                        itemWidth: 80,
                        itemHeight: 20,
                        itemOpacity: 0.75,
                        symbolSize: 12,
                        symbolShape: 'circle',
                        symbolBorderColor: 'rgba(0, 0, 0, .5)',
                        effects: [
                            {
                                on: 'hover',
                                style: {
                                    itemBackground: 'rgba(0, 0, 0, .03)',
                                    itemOpacity: 1
                                }
                            }
                        ]
                    }
                ]}
            />
            </div>


        </React.Fragment>
    );
}

export default App;
